(select null as parent, u.name as student, u.phone as phone, g.name as grade from users u, students s, grades g where s.user_id = u.id and g.id = s.grade_id and state = 'VALIDATION') union (select u.name as parent, us.name as student, u.phone, g.name as grade from users u, users us, parents p, relations r, clients c, students s, grades g where u.id = p.user_id and r.parent_id = p.id and r.child_id = c.id and u.state = 'VALIDATION' and s.user_id = c.user_id and s.grade_id = g.id and us.id = s.user_id);
